package main

import (
	"fmt"

	"github.com/ibiscum/Go-Programming-Cookbook-Second-Edition/chapter07/internal/grpc/greeter"
	"golang.org/x/net/context"
)

// Greeter implements the interface
// generated by protoc
type server struct {
	greeter.UnimplementedGreeterServiceServer
	Exclaim bool
}

// Greet implements grpc Greet
func (g *server) Greet(ctx context.Context, r *greeter.GreetRequest) (*greeter.GreetResponse, error) {
	msg := fmt.Sprintf("%s %s", r.GetGreeting(), r.GetName())
	if g.Exclaim {
		msg += "!"
	} else {
		msg += "."
	}
	return &greeter.GreetResponse{Response: msg}, nil
}
